mapper.insert:
insert into work_image(
	attachment,
	work
) 
values(
	#{attachment}, 
	#{work} 
)

mapper.update:
update auction
auction_schedule_no=#{auctionScheduleNo}, work_no=#{workNo}, auction_success_bidder=#{auctionSuccessBidder}, auction_lot=#{auctionLot}, auction_low_price=#{auctionLowPrice}, auction_high_price=#{auctionHighPrice}, auction_start_date=#{auctionStartDate}, auction_end_date=#{auctionEndDate}, auction_state=#{auctionState}, auction_consigner=#{auctionConsigner}, auction_consignment_fee=#{auctionConsignmentFee}, auction_net_proceeds=#{auctionNetProceeds}, auction_bid_price=#{auctionBidPrice}, auction_bid_increment=#{auctionBidIncrement}, auction_bid_cnt=1 auction_bid_cnt=auction_bid_cnt+1
where auction_no=#{auctionNo}
