mapper.insert:
insert into auction_schedule_image(
	auction, 
	attachment
) 
values(
	#{auction}, 
	#{attachment, jdbcType=INTEGER}
)

mapper.update:
update auction_schedule
auction_schedule_title = #{auctionScheduleTitle}, auction_schedule_start_date = #{auctionScheduleStartDate}, auction_schedule_end_date = #{auctionScheduleEndDate}, auction_schedule_state = #{auctionScheduleState}, auction_schedule_notice = #{auctionScheduleNotice}
where auction_schedule_no = #{auctionScheduleNo}

UPDATE auction_schedule_image
SET 
    attachment = #{attachment}
WHERE
    auction = #{auctionScheduleNo}
