mapper.insert:
insert into items(
	item_id,item_name,is_win,
	item_value,chance
) 
values(
	items_seq.nextval,#{itemName},#{isWin},
	#{itemValue},#{chance}
)

insert into items(
	item_id,item_name,is_win,
	item_value,chance
) 
values(
	#{insertId},#{itemName},#{isWin},
	#{itemValue},#{chance}
)

mapper.update:
update items set item_name=#{itemName},is_win=#{isWin},
item_value=#{itemValue},chance=#{chance}
where item_id=#{itemId}
