mapper.insert:
INSERT INTO member (
	member_id, member_pw, member_name,
	member_email, member_contact, member_post, 
	member_address1, member_address2

) VALUES (
	#{memberId}, #{memberPw}, #{memberName}, 
	#{memberEmail}, #{memberContact}, #{memberPost},
	#{memberAddress1}, #{memberAddress2}
)

mapper.update:
UPDATE member
member_pw=#{memberPw}, member_name=#{memberName}, member_email=#{memberEmail}, member_contact=#{memberContact}, member_post=#{memberPost}, member_address1=#{memberAddress1}, member_address2=#{memberAddress2},
WHERE member_id = #{memberId}
