mapper.insert:
insert into work(
	work_no, artist_no, work_title, work_description, 
	work_materials, work_size, work_category
) 
values(
	#{workNo}, #{artistNo}, #{workTitle}, #{workDescription}, 
	#{workMaterials}, #{workSize}, #{workCategory}
)

insert into work_image(
	attachment, work
) values (
	#{attachment}, #{work}
)

mapper.update:
update work
work_title = #{workTitle}, artist_no = #{artistNo}, work_description = #{workDescription}, work_materials = #{workMaterials}, work_size = #{workSize}, work_category = #{workCategory}
where work_no = #{workNo}
